function [A] = modify_matrix(B)
    s = size(B)
    m = s(1)
    n = s(2)
    A = zeros(n,m);
    
    for i=1:n
        for j=1:m
            A(i,j)=(B(i,j)>= 0)*(B(i,j)*3) + (B(i,j) < 0)*(B(i,j) + 6);
        end
    end
end

function [A] = create_matrix(a,n,m)
    A = zeros(n,m);
    for i=1:n
        for j=1:m
            A(i,j)=a(i,j);
        end
    end
end


function  res = func1(A)
    res = 0
    T = A'
    for x = T
        m1 = x(1)
        m2 = x(2)
        m3 = x(3)
        if m1 < m3
            res = res + 1
        end
        
    end
end

function  res = func2(A)
    res = 0
    T = A'
    for x = T
        m1 = x(1)
        m2 = x(2)
        m3 = x(3)
        if m1 < m2 & m2 < m3
            res = res + 1
        end
        
    end
end

function  res = func3(A)
    res = 0
    T = A'
    for x = T
        m1 = x(1)
        m2 = x(2)
        m3 = x(3)
        if m1 >= 90 | m2 >= 90 | m3 >= 90
            res = res + 1
        end
        
    end
end

